sys_script_client.script:
function onLoad() {
   //Check to see if the form is for a new record.  If it is a new record, 
   //set the Requested for value to the currently logged in user.

    if(g_form.isNewRecord()){
        g_form.setValue('u_reference_4',g_user.userID);
    }

	// Log test
	var x = 7;
 	jslog("TKP ==> This message is from jslog().");   
 	jslog("TKP ==> The value of x = " + x); 
 	jslog("TKP ==> The NeedIt State value = " + g_form.getValue('state'));
 	jslog("TKP ==> The currently logged in user is " + g_user.getFullName() + ".");
	// Console out
	console.log("TKP ==> The value of x = " + x); 

	// Exception test
	try{
		helloWorld();  // Function doesn't exist.
	}
	catch(err){
		jslog('A JavaScript runtime error occurred: ' + err.message);
	}

}
